mapper.update:
UPDATE participants
SET
    score = #{score},
    is_host = #{isHost},
    is_ready = #{isReady}
WHERE participant_id = #{participantId}

mapper.insert:
INSERT INTO participants (
    room_id, user_id, score, is_host, is_ready, created_at
) VALUES (
             #{roomId}, #{userId}, #{score}, #{isHost}, #{isReady}, #{createdAt}
         )
